<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#B2CCD6;">list-style-image</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">url</span><span style="color:#89DDFF;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">sqpurple.gif</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">);</span></span>
